let grid=document.querySelector(".grid"),iso=new Isotope(grid,{itemSelector:".grid-item",layoutMode:"fitRows",sortBy:"marque",sortAscending:!0}),filters=document.querySelector(".filters"),removeActiveLinks=e=>{for(const t of e.getElementsByTagName("a"))t.classList.remove("active")};filters.addEventListener("click",e=>{let t=e.target,r=t.dataset.filter;removeActiveLinks(filters),t.classList.add("active"),iso.arrange({filter:r})});let orders=document.querySelector(".ordering");orders.addEventListener("click",e=>{let t=e.target,r="true"===t.dataset.ordering;removeActiveLinks(orders),t.classList.add("active"),iso.arrange({sortAscending:r})});