"use strict";window.onload=(()=>{d.querySelectorAll("#email, #password").forEach(e=>{e.addEventListener("input",e=>{setApparence(e.currentTarget)})}),d.querySelector("#login-btn").addEventListener("click",e=>{let t=d.querySelector("#loginform"),r=new FormData(t),o=[];for(let e of r)switch(e[0]){case"email":e[1].trim().length&&ValideEmail(e[1])||o.push("email");break;case"password":e[1].trim().length||o.push("password")}if(o.length){for(let e of o)d.querySelector(`#${e}`).classList.add("is-invalid");e.currentTarget.blur()}else fetch("/",{method:"POST",body:r}).then(e=>e.json()).then(e=>{!0===e.connected&&(document.location.href="/")}).catch(e=>{console.error(e)})})});